import React, { useContext } from "react";
import "./CartItems.css";
import { ShopContext } from "../../Context/ShopContext";
import remove_icon from "../Assets/cart_cross_icon.png";

const CartItems = () => {
  const { all_product, cartItems, removeFromCart } = useContext(ShopContext);

  // Debugging logs to verify data flow
  console.log("All products:", all_product);
  console.log("Cart items:", cartItems);

  // Render loading state for undefined or null values
  if (!all_product || !cartItems) {
    return <div>Loading...</div>;
  }

  return (
    <div className="cartitems">
      <div className="cartitems-format-main">
        <p>Products</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />
      {all_product.map((product) => {
      const quantity = cartItems[product.id] || 0; // Retrieve quantity
      if (quantity > 0) {
        // Ensure price and new_price have default values if undefined
        const price = product.new_price ?? product.price ?? 0; // Default to 0 if both are undefined
        const totalPrice = price * quantity;

    return (
      <div key={product.id}>
        <div className="cartitems-format cartitems-format-main">
          <img
            className="carticon-product-icon"
            src={product.image}
            alt={product.name || "Product Image"}
          />
          <p>{product.name || "Unnamed Product"}</p>
          <p>${price.toFixed(2)}</p> {/* Safely handles price */}
          <button className="cartitems-quantity">{quantity}</button>
          <p>${totalPrice.toFixed(2)}</p> {/* Safely handles totalPrice */}
          <img
            src={remove_icon}
            onClick={() => removeFromCart(product.id)}
            alt="Remove"
            className="carticons-remove-icon"
          />
        </div>
        <hr />
      </div>
    );
  }
  return null;
})}

    </div>
  );
};

export default CartItems;
